INSERT INTO reviews(id, course_id, rating, author, text, created_at, status, poster_email, poster_sid, upvotes, downvotes) VALUES('r_1760233939996', 'omsb9', NULL, NULL, '(Dr.Chance) I think he is very good at actual philosophy classes, and this class is more based on stats. He doesn''t really know how to solve your problems if you don''t know something( usually it''s better to ask AI for help) He gives B+ to A for the big writing assignments. It''s very weird how this course curves the midterms and finals, but i think Dr.Chance usually gives A‚Äôs for them.', '2025-10-12T01:52:19.996Z'::timestamptz, 'published', NULL, NULL, 0, 0) ON CONFLICT (id) DO NOTHING;
