INSERT INTO reviews(id, course_id, rating, author, text, created_at, status, poster_email, poster_sid, upvotes, downvotes) VALUES('r_1760300913110', 'oe009', NULL, NULL, '(Dr. Morphew) is a great teacher. He doesn‚Äôt yap, he goes into deep thought of the material. Paying attention to how he thinks about and analyzes the text is crucial, and if you are able to do the same then you will get a good grade in his class. Go to office hours for each essay beforehand and afterward, and utilize the WTC, then it‚Äôs highly possible to get an A in his class.', '2025-10-12T20:28:33.110Z'::timestamptz, 'published', NULL, NULL, 0, 0) ON CONFLICT (id) DO NOTHING;
